(()=>{const e=document.getElementById("decimalInput"),t=document.getElementById("convert-button"),r=document.getElementById("binary-raw"),n=document.getElementById("binary-grouped"),d=document.getElementById("hexadecimal-grouped"),i=document.getElementById("hexadecimal");t.addEventListener("click",(()=>{binary=dec_to_binary(Number(e.value)),r.innerText="binary: "+binary,tempStr="grouped: ",grouped_binary=group_binary(binary);for(let e=0;e<grouped_binary.length;e++)tempStr+=grouped_binary[e]+" ";n.innerText=tempStr,tempStr="hex (grouped): ";for(let e=0;e<grouped_binary.length;e++)tempStr+=String(binary_to_hex(grouped_binary[e]))+" ";d.innerText=tempStr,tempStr="final: 0x";for(let e=0;e<grouped_binary.length;e++)tempStr+=String(hex_lookup[binary_to_hex(grouped_binary[e])]);i.innerText=tempStr}))})();